import React from "react";
import styles from "./Users.module.css";
import axios from 'axios';
import autoPhoto from "../../assets/images/autoPhoto.png"


class Users extends React.Component {
    componentDidMount() {
        axios.get(`http://localhost:3008/users?page=${this.props.currentPage}&count=${this.props.pageSize}`).then(response => {
            this.props.setUsers(response.data.items);
            this.props.setTotalUserCount(response.data.totalUserCount);
        });
    }

    onPageChanged = (number) => {
        this.props.setCurrentPage(number);
        axios.get(`http://localhost:3008/users?page=${this.props.currentPage}&count=${this.props.pageSize}`).then(response => {
            this.props.setUsers(response.data.items);
        });
    }

    render() {

        let pagesCount = this.props.totalUserCount / this.props.pageSize;
        let pages = [];
        for(let i=1; i <= pagesCount; i++) {
            pages.push(i)
        }
        console.log(pagesCount)

        return <div>
                <div>
                    {pages.map(number => {
                        return <span onClick={(e) => {this.onPageChanged(number)}}
                                     className={this.props.currentPage === number ? styles.selectorPage : undefined}>
                            {number}
                        </span>
                    })}
                </div>


                {this.props.users.map((user) => (
                        <div key={user.id}>

                                <span>
                <div>
                    <img src={user.photos.small != null ? user.photos.small : autoPhoto} className={styles.photo}
                         alt=""/>
                </div>
                <div>
                    {user.followed ? <button onClick={() => {
                        this.props.follow(user.id)
                    }}>unfollow</button> : <button onClick={() => {
                        this.props.unfollow(user.id)
                    }}>follow</button>}
                </div>
                        </span>
                            <span>
                <div>{user.fullName}</div>
                <div>{user.status}</div>
            </span>
                            <span>
                <div>{"u.location.country"}</div>
                <div>{"u.location.city"}</div>
            </span>
                        </div>
                    )
                )}
            </div>
    }
}


export default Users;
