import React from "react";
import styles from "./Users.module.css";
import axios from 'axios';
import autoPhoto from "../../assets/images/autoPhoto.png"


class Users extends React.Component {
    componentDidMount() {
        axios.get("http://localhost:3008/users").then(response => {
            this.props.setUsers(response.data.items);
        });
    }

    render() {

        let pagesCount = this.props.totalUserCount / this.props.pageSize;
        console.log(pagesCount)
        let pages = [];
        for(let i=1; i <= pagesCount; i++) {
            pages.push(i)
        }

        return <div>
                <div>
                    {/*{pages.map(number => {*/}
                    {/*    return <span className={this.props.currentPage === number && styles.selectorPage}>{number}</span>*/}
                    {/*})}*/}
                    <span className={styles.selectorPage}>1</span>
                    <span>2</span>
                </div>


                {this.props.users.map((user) => (
                        <div key={user.id}>

                                <span>
                <div>
                    <img src={user.photos.small != null ? user.photos.small : autoPhoto} className={styles.photo}
                         alt=""/>
                </div>
                <div>
                    {user.followed ? <button onClick={() => {
                        this.props.follow(user.id)
                    }}>unfollow</button> : <button onClick={() => {
                        this.props.unfollow(user.id)
                    }}>follow</button>}
                </div>
                        </span>
                            <span>
                <div>{user.fullName}</div>
                <div>{user.status}</div>
            </span>
                            <span>
                <div>{"u.location.country"}</div>
                <div>{"u.location.city"}</div>
            </span>
                        </div>
                    )
                )}
            </div>
    }
}


export default Users;
