const ADD_POST = 'ADD-POST';
const UPDATE_POST = 'UPDATE-POST';

const initialState = {
    postData: [
        {id: 1, text: "Hello"},
        {id: 2, text: "Hello, girl"},
        {id: 3, text: "Hello, friend"},
    ],
    newPostText: 'react',
}

const profileReducer = (state = initialState, action) => {

    switch (action.type) {
        case ADD_POST:
            let newPost = {
                id: 4,
                text: state.newPostText,
            };
            let stateCopy1 = {...state};
            stateCopy1.postData = [...state.postData];
            stateCopy1.postData.push(newPost);
            stateCopy1.newPostText = '';
            return stateCopy1;
        case UPDATE_POST:
            let stateCopy2 = {...state};
            stateCopy2.postData = [...state.postData];
            stateCopy2.newPostText = action.text;
            return stateCopy2;
        // return state;
        default:
            return state;
    }
}

export const AddPostActionCreator = () => ({type: ADD_POST});

export const UpDatePostActionCreator = (postText) => ({
    type: UPDATE_POST, text: postText});

export default profileReducer;
