import React from "react";
import styles from "./Users.module.css";
import axios from 'axios';
import autoPhoto from "../../assets/images/autoPhoto.png"

const Users = (props) => {

    const getUsers = () => {
        if(props.users.length === 0) {
        axios.get("http://localhost:3004/users").then( response => {
            props.setUsers(response.data.items);
        });
    }
    }



    return <div>

        <button onClick={getUsers}>Ger Users</button>

        {props.users.map ( (user) => <div key={user.id}>
            <span>
                <div>
                    <img src={user.photos.small != null ? user.photos.small : autoPhoto} className={styles.photo} alt=""/>
                </div>
                <div>
                    {user.followed ? <button onClick={ () => {props.follow(user.id)}}>unfollow</button> : <button onClick={ () => {props.unfollow(user.id)}}>follow</button> }
                </div>
            </span>
            <span>
                <div>{user.fullName}</div>
                <div>{user.status}</div>
            </span>
            <span>
                <div>{"u.location.country"}</div>
                <div>{"u.location.city"}</div>
            </span>
        </div>
        )}
    </div>;
};

export default Users;
