import React, {createRef} from "react";
import styles from "./AddMessage.module.css";
import {AddMessageActionCreator, UpDateMessageActionCreator} from "../../redux/dialogs-reducer";
import AddMessage from "./AddMessage";
import StoreContext from "react-redux";

const AddMessageContainer = (props) => {



    return (
        <StoreContext.Consumer>
            {
                store => {
                    const state = store.getState();

                    const addNewMessage = () => {
                        store.dispatch(AddMessageActionCreator());
                    }

                    const onChange = (text) => {
                        store.dispatch(UpDateMessageActionCreator(text));
                    }
                    return (
                        <AddMessage
                            AddMessage={addNewMessage}
                            UpDateMessage={onChange}
                            newMessageText={state.dialogsPage.newMessageText}
                        />
                    )
                }
            }
        </StoreContext.Consumer>
    )
}

const mapStateToProps = (state) => {
    return {
        newMessageText: state.dialogsPage.newMessageText,
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        AddMessage: () => {
            dispatch(AddMessageActionCreator());
        },
        UpDateMessage: (text) => {
            dispatch(UpDateMessageActionCreator(text));
        }
    }
}

const AddMessageContainerOne = connect(mapStateToProps, mapDispatchToProps) (AddMessage);

export default AddMessageContainerOne;
