const ADD_MESSAGE = 'ADD-MESSAGE';
const UPDATE_MESSAGE = 'UPDATE-MESSAGE';

const initialState = {
    dialogData: [
        {id: 1, name: "Alex"},
        {id: 2, name: "Petya"},
        {id: 3, name: "Olga"},
        {id: 4, name: "Vanya"},
        {id: 5, name: "Petya"},
        {id: 6, name: "Tanya"}
    ],
    messagesData: [
        {id: 1, message: "Hello"},
        {id: 2, message: "No"},
        {id: 3, message: "Yes"},
        {id: 4, message: "Hello"},
        {id: 5, message: "No"},
        {id: 6, message: "Yes"}
    ],
    newMessageText: 'react',
}

const dialogsReducer = (state = initialState, action) => {

    switch (action.type) {
        case ADD_MESSAGE:
            console.log(state.newMessageText)
            const newMessage = {
                id: 5,
                message: state.newMessageText,
            }

            let copyState1 = {...state};
            copyState1.MessagesData = [...state.messagesData];


            copyState1.MessagesData.push(newMessage);
            copyState1.newMessageText = '';
            return copyState1;
        case UPDATE_MESSAGE:

            let copyState2 = {...state};

            copyState2.newMessageText = action.message;
            return copyState2;
        default:
            return state;
    }

}

export const AddMessageActionCreator = () => ({type: ADD_MESSAGE});

export const UpDateMessageActionCreator = (message) => ({
    type: UPDATE_MESSAGE, message: message});


export default dialogsReducer;
