const ADD_MESSAGE = 'ADD-MESSAGE';
const UPDATE_POST = 'UPDATE-POST';

const store =  {
    _state: {
        profilePage: {
            postData: [
                {id: 1, message: "Hello"},
                {id: 2, message: "Hello, girl"},
                {id: 3, message: "Hello, friend"},
                {id: 4, message: "Hello, brother"},
                {id: 5, message: "Hello"},
                {id: 6, message: "Hello"}
            ],
            newPostData: 'react',
        },
        dialogsPage: {
            dialogData: [
                {id: 1, name: "Alex"},
                {id: 2, name: "Petya"},
                {id: 3, name: "Olga"},
                {id: 4, name: "Vanya"},
                {id: 5, name: "Petya"},
                {id: 6, name: "Tanya"}
            ],
            messagesData: [
                {id: 1, message: "Hello"},
                {id: 2, message: "No"},
                {id: 3, message: "Yes"},
                {id: 4, message: "Hello"},
                {id: 5, message: "No"},
                {id: 6, message: "Yes"}
            ],
            newPostText: 'react',
        },
        sideBar: [
            {id:1, name: 'Olga'},
            {id:2, name: 'Alex'},
            {id:3, name: 'Vanya'}
        ],
    },
    _callSubscriber()  {
        console.log('hello')
    },

    subscribe(observer) {
        this._callSubscriber = observer;
    },
    getState() {
        return this._state;
    },

    dispatch (action) {
        if (action.type === 'ADD-MESSAGE') {
            const newPost = {
                id: 5,
                message: this._state.dialogsPage.newPostText,
            }
            this._state.dialogsPage.messagesData.push(newPost);
            this._callSubscriber(this._state);
            this._state.dialogsPage.newPostText = '';
        } else if (action.type === 'UPDATE-POST'){
            this._state.dialogsPage.newPostText = action.postMessage;
            this._callSubscriber(this._state);
        }
    },
}

export const AddMessageActionCreator = () => ({type: ADD_MESSAGE});

export const UpDatePostActionCreator = (newtText) => ({
    type: UPDATE_POST, postMessage: newtText});

export default store;
