import React from "react";
import styles from "./Dialogs.module.css"
import DialogItem from "./DialogItem/DialogItem";
import Message from "./Message/Message";
import AddMessageContainer from "../AddMessage/AddMessageContainer";
import connect from "react-redux";


const Dialogs = (props) => {

    const messagesData = props.messagesData;
    const dialogData = props.dialogData;

        return(
            <div className={styles.dialogs}>
                <div className={styles.dialogsItem}>
                    {dialogData.map(dialog => <DialogItem
                        name={dialog.name}
                        id={dialog.id}
                    />)}
                </div>
                <div className={styles.messages}>
                    {messagesData.map(m => <Message message={m.message}/>)}
                    <AddMessageContainer />
                </div>
            </div>
        )
}

const mapStateToProps = function(state) {
    return {
        messagesData: state.dialogsPage.messagesData,
        dialogData: state.dialogsPage.dialogData,
    }
}

const DialogsWithProps = connect(mapStateToProps)(Dialogs);

export default DialogsWithProps;
